INSERT INTO `product_translations` (`id`, `product_id`, `name`, `unit`, `description`, `lang`, `created_at`, `updated_at`) VALUES
(2, 2, 'QURAN MAJID', 'pc', '<p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\"><span style=\"font-weight: 700;\">QUR\'AN MAJID</span></p><p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\">With Arabic Text &amp; English Translation</p><p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\">Translated by</p><p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\">Dr. M. Mustafizur Rahman</p>', 'en', '2024-01-26 19:48:25', '2024-01-26 19:48:25'),
(3, 3, 'The Holy Quran', 'pc', '<p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\">The Holy Quran&nbsp;<br></p><p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\">with English and Bangali Translation by&nbsp;<span style=\"caret-color: rgb(5, 5, 5); color: rgb(5, 5, 5); font-family: system-ui, -apple-system, BlinkMacSystemFont, &quot;.SFNSText-Regular&quot;, sans-serif; white-space-collapse: preserve;\">Dr. M. M. Rahman.</span></p>', 'en', '2024-01-26 20:40:30', '2024-01-26 20:40:30'),
(4, 4, 'বাংলা উচ্চারণ, অনুবাদ ও শানে নুযূলসহ কুরআন শরীফ', 'pc', '<div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">শানে নুযুল জানার মাঝে অনেক উপকারীতা রয়েছে। আল্লামা সুয়ূতী (র) ইতকান গ্রন্থে শানে নুযুল জানার কয়েকটি বিশেষ উপকারীতা বর্ণনা করেছেন। </div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">১। শানে নুযুল জানার মাধ্যমে আয়াতের মর্মার্থ খুব সহজে জানা যায় এবং বাক্যের অর্থ বুঝার ব্যাপারে সব সমস্যা দূরীভূত হয়। </div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">২। শরীয়তের বিধানাবলীকে বিধান হিসেবে প্রয়োগ করার মূল রহস্য অবগত হওয়া। শানে নুযুল জানার মাধ্যমে শরীয়তের বিধি- বিধানসমুহের কারণ ও রহস্য জানা যায়। </div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">৩। যে সব মুফাসসির মনে করেন যে, অবতারিত আয়াতের হুকুম বা বিধান শানে নুযুলের সাথে নির্দিষ্ট, <span style=\"font-size: 14px; font-family: inherit;\"><a tabindex=\"-1\" style=\"color: rgb(56, 88, 152); text-decoration-line: underline; touch-action: manipulation; display: inline-block; cursor: pointer; font-family: inherit;\"></a></span>তাদের মতানুযায়ী সে বিধানকে নির্দিষ্ট করতে হলে ঐ আয়াতের শানে নুযুল জানা অত্যাবশ্যক। নতুবা সে হুকুমকে খাস করা সম্ভব হবে না। </div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">৪। কোন কোন সময় কুরআনের শব্দ ব্যাপক অর্থবোধক হয়,কিন্তু ঐ শব্দকে নির্দিষ্ট করার দলীলও বিদ্যমান থাকে। এমতাবস্থায় আয়াত নাযিলের কারন জানা থাকলে অন্যান্য বাহ্যিক অর্থ থেকে দলীলের মাধ্যমে হুকুমকে নির্দিষ্ট করা যায়।</div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">৫। আল্লামা ইবনে তাইমিয়া (র) বলেন-শানে নুযুল জানা কুরআনে কারীমের আয়াতের অর্থ বুঝার জন্য সহায়ক। শানে নুযুল হল আয়াতের নাযিলের কারন। সুতরাং শানে নুযুল জানা থাকলে আয়াতের অর্থ অনায়াসেই বুঝা যাবে। </div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">৬। আল্লামা ওয়াহেদী (র) বলেন- কুরআনের আয়াতের শানে নুযুল এবং ঘটনা জানা ছাড়া কোন আয়াতের তাফসীর জানা সম্ভব নয়। </div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\">৭। কেউ কেউ বলেন- কুরআনুল কারীমের মর্মার্থ বুঝার জন্য শানে নুযুল জানা একটি শক্তিশালী পন্থা। কোন কোন মুফাসসির মনে করেন, পবিত্র কুরআন হল সর্বকালের সর্বযুগের মানুষের সার্বিক সমস্যার একমাত্র সমাধান গ্রন্থ। সুতরাং এর অর্থ অত্যন্ত ব্যাপক ও সর্বজনীন। এর অর্থকে শানে নুযুল এর মাধ্যমে কোন নির্দিষ্ট অর্থে খাস করা ঠিক নয়। সুতরাং শানে নুযুল জানার প্রয়োজনীয়তা নেই। আল্লামা সুয়ূতী (র) তাদের মতামতকে প্রত্যাখান করে বলেন- যারা মনে করেন কুরআনের শানে নুযূল জানার মধ্যে তেমন কোন উপকারিতা নেই তারা এ ব্যাপারে ভুল করেছেন। বরং এটা জানার মাঝে অনেক উপকারীতা রয়েছে। কেননা পবিত্র কুরআন যদিও সর্বজনীন ও ব্যাপক বটে তবুও এর অধিকাংশ আয়াত ও সূরা তৎকালীন মানব সমাজ ও ঘটনাবলির প্রেক্ষিতে অবতীর্ণ হয়। তা না হলে বিনা কারণে ও বিনা প্রেক্ষাপটে কুরআন নাযিল হলে এর গুরুত্ব তেমন অনুধাবন হত না। বরং তখন কুরআনের প্রতি অপবাদ দেয়া হত যে,এটা ঐতিহাসিক কোন প্রেক্ষাপট ছাড়াই বিনা কারণে নাযিল হয়েছে। অতএব কুরআনের শানে নুযুল জানার মাঝ অনেক উপকারিতা রয়েছে এতে কোন সন্দেহ নেই।</div><div dir=\"auto\" style=\"white-space-collapse: preserve; font-family: &quot;Segoe UI Historic&quot;, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif; color: rgb(5, 5, 5); font-size: 15px;\"><br></div><div dir=\"auto\" style=\"\"><font color=\"#050505\" face=\"Segoe UI Historic, Segoe UI, Helvetica, Arial, sans-serif\"><span style=\"font-size: 15px; white-space-collapse: preserve;\">প্রকাশক:মহীউদ্দীন আহমদ \r\nখোশরোজ কিতাব মহল লিমিটেড\r\n১৫ বাংলাবাজার,ঢাকা ১১০০\r\nফোন:01324711304/01325096023\r\nমূল্য: আটশত টাকা \r\nE-mail:khoshrozkitabmahalltd@gmail.com\r\nweb:www.khoshrozltd.com\r\nfacebook:/KhoshrozKitabMahal\r\nপূনর্মুদ্রণঃ২০২০\r\nসম্পূর্ণ-৩০ পাড়া \r\nISBN:984-488-024-3\r\nকাগজ: অফসেট ৫৫ গ্রাম সাদা কাগজ\r\nপৃষ্ঠা :৮৪৪\r\nবান্ডিং :hard cover</span></font><br></div>', 'en', '2024-01-26 21:01:40', '2024-01-26 21:05:43'),
(5, 5, 'Answers From The Quran', 'Pc', '<h2 style=\"font-family: Montserrat; font-weight: 700; line-height: 1.1; color: rgb(51, 51, 51); margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 30px;\">Answers From The Quran</h2><p class=\"MsoNormal\" style=\"margin-right: 0px; margin-bottom: 0in; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;; line-height: normal;\"><span style=\"font-size: 16pt; font-family: Kalpurush;\">আল্লাহর সাথে মানুষের সম্পর্ক বিষয়ে গভীর কিছু প্রশ্ন জাগে মনে। সেই প্রশ্ন কেবলমাত্র চিন্তাশীল মনেই জাগতে পারে। কারণ আল্লাহ হচ্ছেন মহাবিশ্বের স্রষ্টা-প্রতিপালক। মানুষ তার ক্ষুদ্রতম সৃষ্টিমাত্র। কী করে সেই মহাপ্রভুর সাথে ক্ষুদ্র এ বান্দার সম্পর্ক স্থাপিত হতে পারে— সে এক বিস্ময় বটে। আল্লাহর সাথে বান্দার সম্পর্ক তৈরি হওয়া, তার সম্পর্কের গভীরতা সৃষ্টি হওয়া, মহান মনিবের সাথে বান্দার প্রেম এবং তার স্বরূপ কী হতে পারে— ইত্যাদি নানাবিধ প্রশ্নে সমাধান ব্যতিত আস্তিক ও চিন্তাশীল মনন কখনোই প্রশান্ত হতে পারে না। কিন্তু জ্ঞানবান সত্যসন্ধানী মানুষেরা আল্লাহর কিতাবের মধ্যেই সেই মহাসত্যের সন্ধান লাভ করে থাকেন।<o:p></o:p></span></p><p style=\"margin-right: 0px; margin-bottom: 15px; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;;\"></p><p class=\"MsoNormal\" style=\"margin-right: 0px; margin-bottom: 0in; margin-left: 0px; font-size: 15px; color: rgb(51, 51, 51); font-family: &quot;Nunito Sans&quot;; line-height: normal;\"><span style=\"font-size: 16pt; font-family: Kalpurush;\">আল্লাহর প্রতি ভালোবাসা, তাঁর ভয় হৃদয়ে পোষণ করা, আল্লাহর ওপর সন্তুষ্ট থাকা, তাঁর নৈকট্যলাভ, বিপদাপদে আল্লাহর সাহায্য লাভ, জীবন-মৃত্যুর গভীর চিন্তা— এসব অসংখ্য বিষয়ে যেসব প্রশ্ন একজন আল্লাহ বিশ্বাসীর মনে জাগতে পারে, যেসব বিষয়ে সংশয় ও ভ্রান্তি দেখা দিতে পারে, এমনসব বিষয় নিয়ে রচিত বিশ্লেষণধর্মী গ্রন্থ ‘<span style=\"font-weight: 700;\">Answers From The Quran’&nbsp;</span>যার অনুবাদ বাংলায় ‘কোরআন থেকে জবাব’। মূল গ্রন্থ ও বঙ্গানুবাদ— দুটিই প্রকাশ করেছে খোশরোজ কিতাব মহল। বিশ্বখ্যাত চিন্তাবিদ হারুন ইয়াহিয়ার লেখা এই গ্রন্থে কোরআনের আয়াতসমূহ থেকে সেইসব মণিমুক্তা আহরণ করা হয়েছে। গ্রন্থটি সকল সত্যসন্ধানী পাঠকের পাঠ করা কর্তব্য।</span></p>', 'en', '2024-01-26 21:11:00', '2024-01-29 20:13:23'),
